        let beta = opt.beta || 0.9,
            epsilon = opt.epsilon || 1e-8;
        this.initParams();
        for (let i = 0; i < itrns; i++) {
            let [dw, db] = this.Props(this.features, this.labels);
            this.updateProcess(beta);
